        [HttpGet("MemberInvitationAccept/{InvitationToken}")]
       
        public async Task<IActionResult> MemberInvitationAccept(string InvitationToken)
        {
            //var identity = HttpContext.User.Identity as ClaimsIdentity;
            var identity = new JwtSecurityToken(jwtEncodedString: InvitationToken);
            
            if (identity != null)
            {
                var userClaims = identity.Claims;
                int GroupId = Convert.ToInt32(userClaims.FirstOrDefault(x => x.Type == "GroupId").Value);
                string Email = userClaims.FirstOrDefault(x => x.Type == "Email").Value;
                var mbr = _unitOfWork.GroupMember.GetT(x => x.GroupId == GroupId && x.Email == Email && x.IsActive==true && x.IsDeleted==false);
                bool res = await _unitOfWork.MemberInvitation.AccectInvitation(mbr);
                if (res)
                {
                   // return RedirectResult("https://rxsplitapp.azurewebsites.net/#/Login/" + InvitationToken);
                    return new RedirectResult(url: "https://rxsplitapp.azurewebsites.net/#/Login/" + InvitationToken, permanent: true,
                             preserveMethod: true);
                    //var groupData = _unitOfWork.Group.GetT(x => x.Id == _unitOfWork.GroupMember.GetT(x => x.GroupId == GroupId && x.Email == Email && x.IsActive == true && x.IsDeleted == false).GroupId);
                    //var userData=_unitOfWork.User.GetT(x=>x.Id==_unitOfWork.Group.GetT(x=>x.Id==_unitOfWork.GroupMember.GetT(x => x.GroupId == GroupId && x.Email == Email && x.IsActive == true && x.IsDeleted == false).GroupId).AddedBy);

                    //await SendUserStatusMail(mbr.Email, userData.Email, groupData.GroupName);
                }
                else
                {
                    return Redirect("");
                }
            }
            return Redirect("https://rxsplitapp.azurewebsites.net/#/Login/" + InvitationToken);        
        }

        [HttpGet("MemberInvitationDecline/{InvitationToken}")]
       
        public async Task<IActionResult> MemberInvitationDecline(string InvitationToken)
        {
            var identity = new JwtSecurityToken(jwtEncodedString: InvitationToken);
            if (identity != null)
            {
                var userClaims = identity.Claims;
                int GroupId = Convert.ToInt32(userClaims.FirstOrDefault(x => x.Type == "GroupId").Value);
                string Email = userClaims.FirstOrDefault(x => x.Type == "Email").Value;
                var mbr = _unitOfWork.GroupMember.GetT(x => x.GroupId == GroupId && x.Email == Email);
                bool res = await _unitOfWork.MemberInvitation.DeclineInvitation(mbr);
                if (res)
                {
                    return new RedirectResult(url: "https://rxsplitapp.azurewebsites.net/#/Login/" + InvitationToken, permanent: true,
                             preserveMethod: true);
                }
                else
                {
                    return Redirect("");
                }
            }
            return Redirect("https://rxsplitapp.azurewebsites.net/#/Login/" + InvitationToken);
        }



